import React, { useState } from "react";
import TaskForm from "./TaskForm";
import "../css/TaskList.css";
import Tarea from "./TaskItem";

function TaskList() {
  const [tareas, setTareas] = useState([]);

  const addTask = (tarea) => {
    if (tarea.text.trim()) {
      tarea.text = tarea.text.trim();

      const newTareas = [...tareas, tarea];
      setTareas(newTareas);
    }
  };
  
  const taskDelete = (id) => {
    const newTareas = tareas.filter((tarea) => tarea.id !== id);
    setTareas(newTareas);
  };

  const taskComplete = id => {
    const newTareas = tareas.map( tarea => {
        if (tarea.id === id) {
            tarea.complete = !tarea.complete;
        }
        return tarea;
        setTareas(newTareas);
    })
  }

  return (
    <>
      <TaskForm onSubmit={addTask} />
      <div className="TaskListContainer">
        {tareas.map((tarea) => (
          <Tarea key={tarea.id} id={tarea.id} text={tarea.text} completada={tarea.completada} taskDelete={taskDelete}/>
        ))}
      </div>
    </>
  );
}

export default TaskList;
