import React, { useState } from "react";
import TaskForm from "./TaskForm";
import "../css/TaskList.css";
import Tarea from "./TaskItem";

function TaskList() {
  const [tareas, setTareas] = useState([]);

  const addTask = (task) => {
    if (task.text.trim()) {
      task.text = task.text.trim();

      const newTasks = [...tasks, task];
      setTareas(newTasks);
    }
  };
  
  const taskDelete = (id) => {
    const newTareas = task.filter((task) => task.id !== id);
    setTareas(newTasks);
  };

  const taskComplete = id => {
    const newTareas = tasks.map( task => {
        if (task.id === id) {
            taks.complete = !task.complete;
        }
    })
  }

  return (
    <>
      <TaskForm onSubmit={addTask} />
      <div className="TaskListContainer">
        {tareas.map((task) => (
          <Tarea key={task.id} id={task.id} text={task.text} completa={task.complete} taskDelete={taskDelete}/>
        ))}
      </div>
    </>
  );
}

export default TaskList;
