import React, {useState} from 'react';
import TaskForm from './TaskForm';
import '../css/TaskList.css';
import Tarea from './TaskItem';

function TaskList() {
    const [tasks, setTask] = useState([]);
    
    const addTask = task => { if (task.text.trim()){task.text = task.text.trim(); 
    const taskState = [task, ...tasks];
    setTask(taskState);
    } }
    return (
        <>
        <TaskForm onSubmit={addTask}></TaskForm>
        <div className='TaskListContainer'>
            {
                task.map((task) => <Tarea text={task.text} completada={Tarea.completada} />)
            }
        </div>
        </>
    );
}

export default TaskList ;
