import React, { useState } from "react";
import TaskForm from "./TaskForm";
import "../css/TaskList.css";
import Tarea from "./TaskItem";

function TaskList() {
  const [tareas, setTareas] = useState([]);

  const addTask = (task) => {
    if (tarea.text.trim()) {
      tarea.text = task.text.trim();

      const newTasks = [...tasks, task];
      setTareas(newTasks);
    }
  };

  const taskDelete = id => {
    const newTareas = tasks.filter( task => task.id !==id);
    setTareas(newTareas);
  }

  return (
    <>
      <TaskForm onSubmit={addTask} />
      <div className="TaskListContainer">
        {tareas.map((tarea) => (
          <Tarea key={tarea.id} id={tarea.id} text={tarea.text} completada={tarea.completada} taskDelete={taskDelete}/>
        ))}
      </div>
    </>
  );
}

export default TaskList;
